using UnityEngine;public class DoorScript : MonoBehaviour{    public delegate void PlayerInDoor(GameObject door);
    public static event PlayerInDoor PlayerInDoorEvent;    private void Start()    {        if (gameObject.name == "EndDoor")            transform.rotation = new Quaternion(0, 180, 0, 0);    }    private void OnTriggerEnter2D(Collider2D other)    {        if (gameObject.name == "EndDoor" && other.tag == "Player")        {            if(PlayerInDoorEvent != null)                PlayerInDoorEvent.Invoke(gameObject);        }    }}